"use strict";var app=angular.module("Rubrica",[]);app.controller("RubricaController",["$scope","$http",function(a,b){var c=a;c.criterios=[],c.report={},c.init=function(){c.getReport(),c.getRubrica()},c.getReport=function(){b({url:"get-active-example-report",method:"post",data:{rid:1}}).success(function(e){c.report=e})},c.getRubrica=function(){b({url:"get-rubrica",method:"post"}).success(function(d){c.criterios=d})},c.checkCriteria=function(){return c.criterios.reduce(function(d,e){return null!=e.select&&d},!0)},c.sendSelection=function(){c.checkCriteria()&&c.criterios.forEach(function(d){var e={cid:d.id,sel:d.select,rid:c.report.id};b({url:"send-criteria-selection",method:"post",data:e}).success(function(){console.log("ok")})})},c.init()}]);
